"use strict";var mySwiper=new Swiper(".swiper-container",{loop:!0,autoplay:!0,pagination:{el:".swiper-pagination"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});function getTwo(e){return e<10?"0"+e:e}mySwiper.el.onmouseover=function(){mySwiper.autoplay.stop()},mySwiper.el.onmouseout=function(){mySwiper.autoplay.start()};var nums=document.getElementsByClassName("num"),future_time=new Date("2020-3-3 21:55:50");function print_time(){var e=new Date,t=Math.ceil((future_time-e)/1e3);if(0<=t)for(var n=Math.floor(t/3600),r=Math.floor(t%3600/60),i=t%60,o=""+getTwo(n)+getTwo(r)+getTwo(i),a=0;a<o.length;a++)nums[a].innerText=o.charAt(a)}var timer=setInterval(print_time,1e3);